# Conversions / Checking
from hashlib import sha256

# input = "0100000081cd02ab7e569e8bcd9317e2fe99f2de44d49ab2b8851ba4a308000000000000e320b6c2fffc8d750423db8b1eb942ae710e951ed797f7affc8892b0f1fc122bc7f5d74df2b9441a42a14695"
spatial_output = "56 241 171 222 203 126 0 26 180 3 174 205 136 90 1 13 90 37 64 84 228 121 211 53 39 62 170 28 189 214 38 116"
# input = ""
input = ""
# input = ""
# input = ""
spatial_output = spatial_output.split(" ")
spatial_output_hex = [hex(int(num)) for num in spatial_output]

utf_input = input.encode('utf-8')
lib = sha256(utf_input).hexdigest()

hashlib_output = [int(lib[i:i+2], 16) for i in range(0,len(lib),2)]

print(f"SHA Lib:   {lib}")
print(f"Spatial output: {spatial_output_hex}")
print(hashlib_output)
print(", ".join([str(num) + ".to[UInt8]" for num in hashlib_output]))
assert len(hashlib_output) == 32
